(4, 'notify-admin-when-someone-subscribes', 'Notify admin when someone subscribes', 'workflow_email', 3, 'workflow', 'Thanks!', '<p>Hi {{NAME}},</p>\n<p>Just one more step before we share the awesomeness from {{SITENAME}}!</p>\n<p>Please confirm your subscription by clicking on <a href=\'{{SUBSCRIBE-LINK}}\'>this link</a></p>\n<p>Thanks!</p>\n', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', '', '', 0, 1, 'a:3:{s:20:\"enable_open_tracking\";s:2:\"no\";s:20:\"enable_link_tracking\";s:2:\"no\";s:11:\"attachments\";a:0:{}}', '2022-07-15 06:22:25', '2022-07-15 06:22:25', NULL),
(5, 'notify-admin-when-campaign-is-sent', 'Notify admin when campaign is sent', 'workflow_email', 4, 'workflow', 'One more contact joins our tribe!', '<p>Hi,</p>\n<p>Your friendly Email Subscribers notification bot here!</p>\n<p>{{NAME}} ({{EMAIL}}) joined our tribe just now.</p>\n<p>Which list/s? {{LIST}}</p>\n<p>If you know this person, or if they are an influencer, you may want to reach out to them personally!</p>\n<p>Later...</p>\n', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', '', '', 0, 1, 'a:3:{s:20:\"enable_open_tracking\";s:2:\"no\";s:20:\"enable_link_tracking\";s:2:\"no\";s:11:\"attachments\";a:0:{}}', '2022-07-15 06:22:25', '2022-07-15 06:22:25', NULL),
(6, 'add-to-main-list-when-someone-registers', 'Add to Main list when someone registers', 'workflow_email', 5, 'workflow', 'Campaign Sent!', '<p>Hi Admin,</p>\n<p>Cron URL has been triggered successfully on {{DATE}} for the email \'{{SUBJECT}}\'. And it sent email to {{COUNT}} recipient(s).</p>\n<p>Best,<br />\nSanskrithi School of Engineering</p>\n', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', '', '', 0, 0, 'a:3:{s:20:\"enable_open_tracking\";s:2:\"no\";s:20:\"enable_link_tracking\";s:2:\"no\";s:11:\"attachments\";a:0:{}}', '2022-07-15 06:22:25', '2022-07-15 06:22:25', NULL),
(7, 'welcome-to-email-subscribers', 'Welcome To Email Subscribers', 'newsletter', NULL, NULL, 'Welcome To Email Subscribers', '<strong style=\"color: #990000\">What can you achieve using Email Subscribers?</strong><p>Add subscription forms on website, send HTML newsletters. You can also Import or Export subscribers from any list to Email Subscribers.</p> <strong style=\"color: #990000\">Plugin Features</strong><ol> <li>Send notification emails to subscribers when new blog posts are published.</li> <li>Subscribe form available with 3 options to setup.</li> <li>Double Opt-In and Single Opt-In support.</li> <li>Email notification to admin when a new user signs up (Optional).</li> <li>Automatic welcome email to subscriber.</li> <li>Auto add unsubscribe link in the email.</li> <li>Import/Export subscriber emails to migrate to any lists.</li> <li>Default WordPress editor to create emails.</li> </ol> <strong>Thanks & Regards,</strong><br/>Admin<br/>', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', '', '', 466, 1, 'a:3:{s:20:\"enable_open_tracking\";s:3:\"yes\";s:20:\"enable_link_tracking\";s:2:\"no\";s:15:\"list_conditions\";a:1:{i:0;a:1:{i:0;a:3:{s:5:\"field\";s:10:\"_lists__in\";s:8:\"operator\";s:2:\"is\";s:5:\"value\";s:1:\"1\";}}}}', '2022-07-15 06:22:25', NULL, NULL),
(8, 'new-post-published-posttitle', 'New Post Published - {{POSTTITLE}}', 'post_notification', NULL, NULL, 'New Post Published - {{POSTTITLE}}', 'Hello {{NAME}},\r\n\r\nWe have published a new blog article on our website : {{POSTTITLE}}\r\n{{POSTIMAGE}}\r\n\r\nYou can view it from this link : {{POSTLINK}}\r\n\r\nThanks & Regards,\r\nAdmin\r\n\r\nYou received this email because in the past you have provided us your email address : {{EMAIL}} to receive notifications when new updates are posted.', 'ajishtmohan@gmail.com', 'Sanskrithi School of Engineering', 'ajishtmohan@gmail.com', 'Sanskrithi School of Engineering', '##1##', '1', 467, 0, 'a:1:{s:15:\"list_conditions\";a:1:{i:0;a:1:{i:0;a:3:{s:5:\"field\";s:10:\"_lists__in\";s:8:\"operator\";s:2:\"is\";s:5:\"value\";s:1:\"1\";}}}}', '2022-07-15 06:22:25', NULL, NULL);
