(561, 1, '2022-07-26 09:51:53', '2022-07-26 09:51:53', '<!-- wp:html -->\n<p>\n          At SSE sports and games activities are organized on a regular basis. \n        </p>\n        <h3 class=\"committee-page-content__main--h3\">Functions</h3>\n        <ul class=\"committee-page-content__main--ul\">\n          <li class=\"committee-page-content__main--li\">\n            It is designed to serve the interests of the student’s community in\n            competitive sports and other recreational activities i.e., both\n            indoor and outdoor.\n          </li>\n          <li class=\"committee-page-content__main--li\">\n            To create zeal amongst students and faculty members towards sports.\n          </li>\n          <li class=\"committee-page-content__main--li\">\n            To enhance the interest of participants in the field of sports.\n          </li>\n          <li class=\"committee-page-content__main--li\">\n            Organize various competitions in the following sports: cricket,\n            basketball, volleyball, table tennis and chess\n          </li>\n        </ul>\n        <h3 class=\"committee-page-content__main--h3\">\n          PT: Mr Ramu +91 6302070194\n        </h3>\n<!-- /wp:html -->', 'Sports &#038; Games', '', 'inherit', 'closed', 'closed', '', '346-revision-v1', '', '', '2022-07-26 09:51:53', '2022-07-26 09:51:53', '', 346, 'http://210.18.180.227/?p=561', 0, 'revision', '', 0),
